properties.property:
//load data into CDW

var cdwContext = new Packages.org.springframework.context.support.ClassPathXmlApplicationContext("classpath*:applicationContext-cacis-cdw.xml");
var loader = cdwContext.getBean("loader");

loader.load(messageObject.getEncodedData(), loader.generateContext(), $('studyId'), $('siteId'), $('patientId'));

logger.info('Loading data into CDW');

step.script:
var clinicalMetaData = msg['clinicalMetaData'];

var siteId = clinicalMetaData['@siteIdRoot'] + clinicalMetaData['@siteIdExtension'];
var studyId = clinicalMetaData['@studyIdRoot'] + clinicalMetaData['@studyIdExtension'];
var patientId = clinicalMetaData['@patientIdRoot'] + clinicalMetaData['@patientIdExtension'];

channelMap.put('siteId', siteId);
channelMap.put('studyId', studyId);
channelMap.put('patientId', patientId);

