step.script:
var exchDocs = msg['routingInstructions']['exchangeDocument'];

var responseMsg = '';

for each (seg in exchDocs) {
	var exFrmt = seg['@exchangeFormat'].toString();
	//TODO : to handle transformations
	var rec = seg['recipient'].child(0);
	var recTyp = rec.localName();
	var url = rec['@url'].toString();
	var chNm;
	if (recTyp == 'secureEmailAddress') {
		chNm = 'DR-4_SecureEmailTransmission';
	} else {
		chNm = recTyp + 'Channel';
	}

	var msgToRoute = msg.copy();
	msgToRoute['routingInstructions']['exchangeDocument']=seg.copy();
	processRoute(chNm, msgToRoute, recTyp, url);
}

responseMap.put('Destination 1', responseMsg);

function processRoute(channelName, msgToRoute, recTyp, url) {	
	var response = router.routeMessage(channelName, msgToRoute, false);
	handleResponse(response, recTyp, url );
}

function handleResponse(response, recTyp, url ) {
	responseMsg = responseMsg 
			+ 'Processed routing for '
			+ recTyp + ', ' + url 
			+ ', with response :' + response.getMessage() + '!';		
}

