function xsdValidateSACaCISRequest(reqMsg) {

	var commonsCtx = globalMap.get('commonsctx');
	try{
		var saXsdValidator = commonsCtx.getBean("cacisRequestSASchemaValidator");
		saXsdValidator.validate(reqMsg);
	}catch(err) {
		return err.message;
	}
    return null;
}

