function formatErrorStr(errStr) {
	return 'CaCISError:' + errStr;
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function handleErrorStr(errStr) {
	var existErr = responseMap.get('error');
	existErr = (existErr==null)?'':existErr;
	existErr = Packages.org.apache.commons.lang.StringUtils.remove(existErr, 'SUCCESS:');
	existErr = existErr + 'CaCISError:' + errStr + '!';
	responseMap.put('error', existErr);
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function handleResponse(resConnectorNm, destArr) {

	var err = responseMap.get('error');

	if(err != null ){
		responseMap.put(resConnectorNm, err);
	} else {
        var res = '';
		if(destArr != null && destArr.length > 0) {
			for (i=0;i<=destArr.length;i++) {
				var currRes = responseMap.get(destArr[i]);	
				if(currRes != null) {			
					res = res + currRes.toString() + '!';
				}
			}//end of for
		} else {
			res = 'Successfully processed!';
		}
		if(Packages.org.apache.commons.lang.StringUtils.contains(res, 'CaCISError')){
			res = Packages.org.apache.commons.lang.StringUtils.remove(res, 'SUCCESS:');
			res = Packages.org.apache.commons.lang.StringUtils.remove(res, '!');
			res = Packages.org.apache.commons.lang.StringUtils.replace(res, '  ', ' ');
		}
		responseMap.put(resConnectorNm, res);
	}
}

function drTransformDocument(documentToTransform, transformerName) {
	var transformer = globalChannelMap.get(transformerName);
	var os;
	var is;
	try {
		os = new Packages.java.io.ByteArrayOutputStream();
		is = new Packages.java.io.ByteArrayInputStream(new Packages.java.lang.String(documentToTransform).getBytes());

		transformer.transform(null, is, os);

		transformedDocument = new Packages.java.lang.String(os.toByteArray());

	} finally {
		if (is != null) {
			is.close();
		}
		if (os != null) {
			os.close();
		}
	}
	return transformedDocument;
}

// modify function_name and parameters as you wish.
// one function per template is recommended. i.e.) create a new code template for each new function.
function filterOnError() {
	var err = responseMap.get('error');

	//return true to accept the message and false to filter it
	if(err == null) {
		return true;
	} else {
		return false;
	}
}

