step.script:
var doctype = msg['clinicalMetaData']['@documentType'].toString();
var cmd = msg['clinicalMetaData'];

if(cmd.length()==0){
	handleErrorStr('ClinicalMetadata is not available');
	return;
}

var siteIdExt = cmd['@siteIdExtension'];
if(siteIdExt.toString().trim()==''){
	handleErrorStr('SiteId Extension is not available');
	return;
}

var patIDExt= cmd['@patientIdExtension'];
if(patIDExt.toString().trim()==''){
	handleErrorStr('PatientId Extension is not available');
	return;
}

