step.script:
var srcDataEle = msg['sourceData'];

//If clinicalMetaData section contains documentType 'COMPLETE',
//sourcedata should contain concatenated <trims> element.
if( Packages.org.apache.commons.lang.StringUtils.equals(doctype,'COMPLETE') ) {
	var trimsEle = srcDataEle['trims'];
	if (trimsEle.length()==0) {
		handleErrorStr('Document type is Complete, sourceData should contain <trims> element ');
		return;
	}
	default xml namespace = 'urn:tolven-org:trim:4.0';
	var trimsArr = trimsEle['trim'];
	if (trimsEle.length()==0) {
		handleErrorStr('Document type is Complete, sourceData <trims> should contain atleast one <trim> element ');
		return;
	}
	for each (trim in trimsArr) {
		performTrimValidation(doctype, trim);
	}		
} else if( Packages.org.apache.commons.lang.StringUtils.equals(doctype,'CLINICAL_NOTE') ) {
	default xml namespace = 'urn:tolven-org:trim:4.0';
	var trim = srcDataEle['trim'];
	if (trim.length()==0) {
		handleErrorStr('sourceData should contain trim element');
		return;
	}

	performTrimValidation(doctype, trim);
} else {
	handleErrorStr('Document type('+ doctype + ') is unknown ');
	return;
}

function performTrimValidation(doctype, trimData) {
	if (! validateTrimDescription(doctype, trimData) ){
		return;
	}
	if(globalChannelMap.get('sa.trim.xsd.validation') == 'true'){
		if (! validateTrimAgainstSchema(trimData) ) {
			return;
		}
	}
	if(! validateTrimAgainstSchematron(trimData) ){
		return;
	}
}

//Perform check to make sure description of the trim is available
//and if document type is clinical note, check desc is of one of the allowed types
function validateTrimDescription(doctype, trimData) {
	var desc = trimData['description'];
	if (desc.length() == 0) {
		handleErrorStr('description is not available !');
		return false;
	}
	var descStr = desc.toString().trim();
	if (descStr=='') {
		handleErrorStr('description is empty !');
		return false;
	}
	if( Packages.org.apache.commons.lang.StringUtils.equals(doctype,'CLINICAL_NOTE')) {
		if( descStr != 'Baseline Evaluation Note' 
			&& descStr != 'Clinical Staging'
			&& descStr != 'Study Visit (Medical)'
			&& descStr != 'Surgical Study Visit Note' ) {
			handleErrorStr('Unknown description for clinical note trim,' + descStr);
			return false;	
		}		
	}//end of validation for clinical note
}

//Perform schema validation for SourceData
function validateTrimAgainstSchema(trimData) {	
	var sdXsdValidationMsg = xsdValidateSASourceData(trimData.toString());
	if (sdXsdValidationMsg != null) { 
		handleErrorStr('SourceData Schema Validation:' + sdXsdValidationMsg);
		return false;
 	}	
}

//Perform schematron validation for SourceData
function validateTrimAgainstSchematron(trimData) {
	var sdSchValidationMsg = schematronValidateSASourceData(trimData.toString());
	if (sdSchValidationMsg != null) { 
		handleErrorStr('SourceData Schematron Validation:' + sdSchValidationMsg);
		return false;
	}
}

