step.script:
var trim2CCDTransformer = globalChannelMap.get('trim2CCDTransformer');
var os;
var is;

try {
	os = new Packages.java.io.ByteArrayOutputStream();
	is = new Packages.java.io.ByteArrayInputStream(new Packages.java.lang.String(msg).getBytes());

	trim2CCDTransformer.transform(null, is, os);

	var temp = new Packages.java.lang.String(os.toByteArray());

	msg = temp;
} finally {
	is.close();
	os.close();
}

